package com.syntax.class02;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * Create a Java program and name it Variable Values
In your program declare variables using different 
ways to store all different primitive data.
Update value of all variables
Print the value of each updated variable
 */
		int num1, num2;
		float num4;
		char gender;
		boolean result;
		
		num1=35;
		num2=7;
		num4=4.7f;
		gender='m';
		result=true;
		
		System.out.println(num1);
		System.out.println(num2);
		System.out.println(num4);
		System.out.println(gender);
		System.out.println(result);
		
		num1=57;
		num2=67;
		num4=8.77f;
		gender='F';
		result=false;
		System.out.println(" ");
		System.out.println("AFTER UPDATE");
		System.out.println(" ");
		System.out.println(num1);
		System.out.println(num2);
		System.out.println(num4);
		System.out.println(gender);
		System.out.println(result);
	}

}
