package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Kaan";
		System.out.println("Good Morning "+name);
		
		int num1=10;
		int num2=11;
		
		// + acts as arithmetic operator
		System.out.println(num1+num2); //11
		
		//Arithmetic 	+, -, /, *, %   Modulus
		
		int a=10;
		int b=3;
								  //System.out.println(1%5); //1
		System.out.println(a/b);  //3  shows result without decimal
		System.out.println(a%b);  //1  Shows the remainder
		
		System.out.println(10%5); //
		
		int result=10+5*5;
		System.out.println(result);
		
		result=(10+5)*5;
		System.out.println(result);
		
		int c=100;
		String d="100";
		
		System.out.println(c+d); //100100
		
		
		
		
	}

}
